recommendedProducts.map:
function(doc) {
    if (doc.type == 'product')
    {
        if (doc.isRecommended){
            emit("recommended", doc);
        } else {
            emit("not recommended", doc);
        }
    }
}

userInfo.map:
function(doc) {
  if (doc.first_name && doc.last_name && doc.username && doc.password && doc.recommended_items && doc.past_order && doc.cart && doc.list && doc.mystore && doc.coupons && doc.type == "user"){
    emit("first_name", doc);
  }
}

featuredDepartments.map:
function(doc) {
    if (doc.type == 'department')
    {
        if (doc.isFeatured){
            emit("featured", doc);
        } else {
            emit("not featured", doc);
        }
    }
}
